import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";

const reviews = [
  {
    name: "Dirk Verhoeven",
    date: "2 months ago",
    rating: 5,
    text: "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Aenean commodo ligula eget dolor. Aenean massa.",
    avatar: "/review-img.svg",
  },
  {
    name: "Dirk Verhoeven",
    date: "2 months ago",
    rating: 5,
    text: "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Aenean commodo ligula eget dolor. Aenean massa.",
    avatar: "/review-img.svg",
  },
  {
    name: "Dirk Verhoeven",
    date: "2 months ago",
    rating: 5,
    text: "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Aenean commodo ligula eget dolor. Aenean massa.",
    avatar: "/review-img.svg",
  },
  {
    name: "Dirk Verhoeven",
    date: "2 months ago",
    rating: 5,
    text: "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Aenean commodo ligula eget dolor. Aenean massa.",
    avatar: "/review-img.svg",
  },
];

function ContactPage() {
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    phone: "",
    email: "",
    message: "",
  });

  const [errors, setErrors] = useState({});

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const validate = () => {
    let tempErrors = {};
    if (!formData.firstName.trim()) tempErrors.firstName = "First name is required";
    if (!formData.lastName.trim()) tempErrors.lastName = "Last name is required";

    if (!formData.phone.trim()) tempErrors.phone = "Phone is required";
    else if (!/^[0-9]{10,15}$/.test(formData.phone)) tempErrors.phone = "Enter a valid phone number";

    if (!formData.email.trim()) tempErrors.email = "Email is required";
    else if (!/\S+@\S+\.\S+/.test(formData.email)) tempErrors.email = "Enter a valid email address";

    if (!formData.message.trim()) tempErrors.message = "Message is required";

    setErrors(tempErrors);
    return Object.keys(tempErrors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validate()) {
      console.log("✅ Form submitted:", formData);
      // API submission here
      alert("Form submitted successfully!");
      setFormData({
        firstName: "",
        lastName: "",
        phone: "",
        email: "",
        message: "",
      });
    }
  };

  return (
    <div className="container my-5">

      {/* Map and Contact Info */}
      <div className="row mb-5">
        <div className="col-md-6 mb-3">
          <iframe
            title="map"
            src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3682.8881862940902!2d88.39992957475849!3d22.620650231208543!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x39f89e075bbf3817%3A0x8bd1a9ec63c9e1aa!2sNeo%20Coderz%20Technologies!5e0!3m2!1sen!2sin!4v1758793112291!5m2!1sen!2sin"
            width="100%"
            height="100%"
            style={{ border: 0, minHeight: "350px" }}
            allowFullScreen
            loading="lazy"
            referrerPolicy="no-referrer-when-downgrade"
          ></iframe>
        </div>

        <div className="col-md-6">
          <h4>Contact</h4>
          <p>Phone: 0492 74 52 17</p>
          <p>Email: info@automotivevanlierop.nl</p>
          <p>Lange Vonder 1 <br />5741 TT Beek en Donk</p>

          <h4 className="mt-4">Openingstijden</h4>
          <div className="mb-3">
            <h6>Showroom</h6>
            <table className="table table-borderless">
              <tbody>
                <tr>
                  <td>Ma - Vr:</td>
                  <td>09:00 - 17:30</td>
                </tr>
                <tr>
                  <td>Za:</td>
                  <td>Gesloten</td>
                </tr>
                <tr>
                  <td>Zo:</td>
                  <td>Gesloten</td>
                </tr>
              </tbody>
            </table>
          </div>

          <div>
            <h6>Werkplaats</h6>
            <table className="table table-borderless">
              <tbody>
                <tr>
                  <td>Ma - Vr:</td>
                  <td>09:00 - 17:30</td>
                </tr>
                <tr>
                  <td>Za:</td>
                  <td>Gesloten</td>
                </tr>
                <tr>
                  <td>Zo:</td>
                  <td>Gesloten</td>
                </tr>
              </tbody>
            </table>
          </div>
        </div>
      </div>

      {/* Contact Form */}
      <div className="row mb-5">
        <div className="col-md-8 mx-auto">
          <h4 className="text-center mb-3">Vragen of interesse?</h4>
          <p className="text-center text-muted mb-4">
            Via het formulier op deze pagina kan eenvoudig een afspraakverzoek worden ingediend. <br />
            Uiteraard kan dit ook telefonisch. Wij zullen een verzoek altijd bevestigen.
          </p>

          <form onSubmit={handleSubmit}>
            <div className="row mb-3">
              <div className="col-md-6 mb-2">
                <input
                  type="text"
                  className={`form-control ${errors.firstName ? "is-invalid" : ""}`}
                  placeholder="Voornaam"
                  name="firstName"
                  value={formData.firstName}
                  onChange={handleChange}
                />
                <div className="invalid-feedback">{errors.firstName}</div>
              </div>
              <div className="col-md-6 mb-2">
                <input
                  type="text"
                  className={`form-control ${errors.lastName ? "is-invalid" : ""}`}
                  placeholder="Achternaam"
                  name="lastName"
                  value={formData.lastName}
                  onChange={handleChange}
                />
                <div className="invalid-feedback">{errors.lastName}</div>
              </div>
            </div>

            <div className="row mb-3">
              <div className="col-md-6 mb-2">
                <input
                  type="text"
                  className={`form-control ${errors.phone ? "is-invalid" : ""}`}
                  placeholder="Telefoon"
                  name="phone"
                  value={formData.phone}
                  onChange={handleChange}
                />
                <div className="invalid-feedback">{errors.phone}</div>
              </div>
              <div className="col-md-6 mb-2">
                <input
                  type="email"
                  className={`form-control ${errors.email ? "is-invalid" : ""}`}
                  placeholder="Email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                />
                <div className="invalid-feedback">{errors.email}</div>
              </div>
            </div>

            <div className="mb-3">
              <textarea
                className={`form-control ${errors.message ? "is-invalid" : ""}`}
                placeholder="Bericht"
                name="message"
                rows="5"
                value={formData.message}
                onChange={handleChange}
              ></textarea>
              <div className="invalid-feedback">{errors.message}</div>
            </div>

            <div className="text-center">
              <button type="submit" className="btn btn-danger px-4 py-2">
                Verzenden
              </button>
            </div>
          </form>
        </div>
      </div>

      {/* Reviews Section */}
      <div className="row">
        {reviews.map((review, index) => (
          <div className="col-md-3 mb-3" key={index}>
            <div className="card h-100 shadow-sm">
              <div className="card-body">
                <div className="d-flex align-items-center mb-2">
                  <img src={review.avatar} alt="avatar" className="rounded-circle me-2" width="50" height="50"/>
                  <div>
                    <h6 className="mb-0">{review.name}</h6>
                    <small className="text-muted">{review.date}</small>
                  </div>
                </div>
                <div className="mb-2">
                  {"⭐".repeat(review.rating)}
                </div>
                <p className="text-muted">{review.text}</p>
                <div className="d-flex align-items-center gap-2">
                  <a href="https://google.com" target="_blank" rel="noopener noreferrer">
                    <img src="https://upload.wikimedia.org/wikipedia/commons/2/2f/Google_2015_logo.svg" alt="Google" width="50" height="50"/>
                  </a>
                  <small className="text-muted">Posted on Google</small>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>

    </div>
  );
}

export default ContactPage;
